mapper.select:
select 

from car
where users_id = #{usersId,jdbcType=INTEGER}
  and goods_id = #{goodsId,jdbcType=INTEGER}

mapper.delete:
delete from car
where users_id = #{usersId,jdbcType=INTEGER}
  and goods_id = #{goodsId,jdbcType=INTEGER}

mapper.insert:
insert into car (users_id, goods_id, goodsnumber
  )
values (#{usersId,jdbcType=INTEGER}, #{goodsId,jdbcType=INTEGER}, #{goodsnumber,jdbcType=INTEGER}
  )

mapper.update:
update car


    goodsnumber = #{goodsnumber,jdbcType=INTEGER},


where users_id = #{usersId,jdbcType=INTEGER}
  and goods_id = #{goodsId,jdbcType=INTEGER}

update car
set goodsnumber = #{goodsnumber,jdbcType=INTEGER}
where users_id = #{usersId,jdbcType=INTEGER}
  and goods_id = #{goodsId,jdbcType=INTEGER}
